import unittest
import lab2_solution as l2
class MyTestCase(unittest.TestCase):
    def test_optimize(self):
        optimized = ""
        self.assertEqual(l2.optimize_gene("YER059W", "Scer.fasta", "Ecol_codon_freqs.csv"), optimized)
        self.assertEqual(l2.optimize_gene("ABCDE", "Scer.fasta", "Ecol_codon_freqs.csv"), -1)

    def test_deoptimize(self):
        deoptimized = ""
        self.assertEqual(l2.deoptimize_gene("YER059W", "Scer.fasta", "Ecol_codon_freqs.csv"), deoptimized)
        self.assertEqual(l2.deoptimize_gene("ABCDE", "Scer.fasta", "Ecol_codon_freqs.csv"), -1)